package coreJava.loopstmts;

public class DoWhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		do
		{
			System.out.println(i);
			
			i++;
			
		}while(i<=10);
		
		System.out.println();
		
		int j=11;
		do
		{
			System.out.println(j);
			
		j++;
			
		}while(j<=10);
	}

}
